import React, { useState } from 'react'


function Location() {

  const [location, setLocation] = useState('')
  const [errors, setErrors] = useState([])

  const handleLocation = (e) => setLocation(e.target.value)

  function onSubmit(e){
    e.preventDefault()
    const newLocation = {
      name: location,
    }
    // console.log(newLocation)

    fetch(`/locations`,{
      method:'POST',
      headers:{'Content-Type': 'application/json'},
      body:JSON.stringify(newLocation)
    })
    .then(res => res.json())
    .then(location => {
      console.log(location)
      if (!location.errors)  { 
        alert("New location added successfully")
      } else {
        setErrors(Object.entries(Location.errors))
      }
    })  
  }

  return (
    <div > 

    <p></p>
    <form onSubmit={onSubmit}>

    {/* <label> Location: </label> */}
      <input 
        style={{ marginLeft:'20px', width: '22%'}} 
        type="text" 
        value={location} 
        placeholder="Location didn't exist? Add it here and try your post again!"
        onChange={(e) => handleLocation(e)}
      />

    <button type="submit">Location</button>

    </form>
      {errors?errors.map(e => <div>{e[0]+': ' + e[1]}</div>):null}
    </div>
  )
}

export default Location
