import React, { useState } from 'react'
// import { useNavigate } from 'react-router-dom';

function Signup() {

  // * Need to set REGEX to ensure valid email address

  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [email, setEmail] = useState('')
  const [name, setName] = useState('')
  const [homeLocation, setHomeLocation] = useState('')
  const [errors, setErrors] = useState([])

  const handleUsername = (e) => setUsername(e.target.value)
  const handlePassword = (e) => setPassword(e.target.value)
  // const navigate = useNavigate();

  function onSubmit(e){
    e.preventDefault()
    const newUser = {
      username: username,
      password: password,
      email: email,
      first_name: name,
      home_location: homeLocation,
    }
    // console.log(newUser)

    fetch(`/signup`,{
      method:'POST',
      headers:{'Content-Type': 'application/json'},
      body:JSON.stringify(newUser)
    })
      .then(res => res.json())
      .then(user => {
        // console.log(user)
        if (!user.errors)  { 
          setUsername('')
          setPassword('')
          setEmail('')
          setName('')
          setHomeLocation('')
          alert("Signup successful")
          // setUser(user)
          // navigate(`/home`)
        } else {
        setErrors(Object.entries(user.errors))
        }
      })  
    }

  return (
    <div> 

      <a href='/' style={{textDecoration: 'none', color: 'black', fontFamily: 'fantasy', fontSize:'20px'}}>
        <h1>Around Town</h1>
      </a>

      <hr></hr>

      {/* <h4>Create Account</h4> */}

      <p style={{marginTop:'10%'}}></p>
      <form onSubmit={onSubmit}>

      {/* <label> Username: </label> */}
        <input 
          style={{width:'20%'}} 
          type="text" 
          value={username} 
          placeholder="USERNAME"
          onChange={(e) => handleUsername(e)} 
        />

      <p></p>
      {/* <label> Password: </label> */}
        <input 
          style={{width:'20%'}} 
          type="password" 
          value={password} 
          placeholder="PASSWORD"
          onChange={(e) => handlePassword(e)} 
        />

      <p></p>
      {/* <label htmlFor="email">Email: </label> */}
        <input
          style={{width:'20%'}}
          type="text"
          id="email"
          value={email}
          placeholder="EMAIL"
          onChange={(e) => setEmail(e.target.value)}
        />

      <p></p>
      {/* <label htmlFor="first_name"> Name: </label> */}
        <input
          style={{width:'20%'}}
          type="text"
          id="first_name"
          value={name}
          placeholder="NAME"
          onChange={(e) => setName(e.target.value)}
        />

      <p></p>
      {/* <label htmlFor="home_location"> Current Location: </label> */}
        <input
          style={{width:'20%'}}
          type="text"
          id="home_location"
          value={homeLocation}
          placeholder="LOCATION | example: New York, New York"
          onChange={(e) => setHomeLocation(e.target.value)}
        />

      <p></p>
      <button type="submit" style={{width: '10%'}}>Signup</button>

      <p></p>

      </form>

      <a style={{textDecoration: 'none'}}href='home'> <button style={{width:'10%'}}>View Home</button> </a>
      {errors?errors.map(e => <div>{e[0]+': ' + e[1]}</div>):null}

      <h5 style={{marginTop:'252px', fontSize: '14px', fontWeight:'lighter'}}>Share events, facts, and other interesting information about your town.</h5>

    </div>
  )
}

export default Signup
