import '../App.css';
import { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";


function App() {


  return (
    <div className="App">

    <Routes>

      <Route path="/home" 
        element={
          <Homepage 
            user={user} 
            setUser={setUser} 
            handleDeleteAccount={handleDeleteAccount} 
            location={location} 
            weatherData={weatherData} 
            handleDeletePosts={handleDeletePosts}
          />
        }
      />

      <Route path="/signup" element={<Signup />}/>

      <Route path="/posts" 
        element={
          <PostForm 
            user={user}  
            location={location} 
            handleDeletePosts={handleDeletePosts} 
            posts={posts}
          />
        } 
      />

      <Route path="/account" 
        element={
          <ProfilePage 
          user={user} 
          setUser={setUser} 
          weatherData={weatherData} 
          location={location} 
          handleDeleteAccount={handleDeleteAccount}
          postContent={postContent}
          setPostContent={setPostContent}
          />
        } 
      />

      <Route path="/" 
        element={
          <FrontPage 
            user={user} 
            setUser={setUser}
          />
        }
      />

    </Routes>

  </div>
  );
}

export default App
